import axios from 'axios';
import React, { useEffect, useState } from 'react';
import {Pie, Doughnut} from 'react-chartjs-2';

const ChartsComponent1 = ({country}) => {

    // const [selectedcountry, setSelectedCountry] = useState(country);
    const [statsData, setStatsData] = useState({country: "", deaths: "", tests: "", cases: "", lastUpdate: ""})
    const [getStats, setGetStats] = useState(false)

    useEffect(() => {
        fetchChartData(country)
    }, [country])
    
    // let statsData = {
    //     country: undefined,
    //     deaths: undefined,
    //     tests: undefined,
    //     cases: undefined,
    //     lastUpdate: undefined
    // };

    const duration = [
        'Last One Week',
        'Last Two Weeks',
        'Last Three Weeks',
        'Last One Month',
    ];

    const CHART_COLORS = {
        red: 'rgb(255, 99, 132)',
        orange: 'rgb(255, 159, 64)',
        yellow: 'rgb(255, 205, 86)',
        green: 'rgb(75, 192, 192)',
        blue: 'rgb(54, 162, 235)',
        purple: 'rgb(153, 102, 255)',
        grey: 'rgb(201, 203, 207)'
      };

    const state = {
      labels: ['Recovered', 'Critical', 'Active', 'New', 'Total'],
      datasets: [
        {
          backgroundColor: Object.values(CHART_COLORS),
        //   data: [
        //         statsData.cases.new,
        //         statsData.cases.critical,
        //         statsData.cases.recoverd,
        //         statsData.cases.active,
        //         statsData.cases.total,
        //   ]
        }
      ]
    };

    const fetchChartData = (country) => {
        if (country !== 'All') {
            
            axios.get(`/api/statistics/${country}`).then(res=>{
                if(res){
                    console.log(res)
                    const data = res.data.response[0]
                    console.log('data' + data)
                    for (let dat in data){

                        setStatsData({
                             country: dat.country,
                             deaths: dat.deaths,
                             tests: dat.tests,
                             cases: dat.cases,
                             lastUpdate: (new Date(dat.time)).toUTCString()
                         });
                    }
                
                    
                    setGetStats(true)
                }
                console.log('stats' + statsData.cases)

    
                //     state = {
                //     labels: ['Recovered', 'Critical', 'Active', 'New', 'Total'],
                //     datasets: [
                //       {
                //         backgroundColor: Object.values(CHART_COLORS),
                //         data: [
                //               statsData.cases.new,
                //               statsData.cases.critical,
                //               statsData.cases.recoverd,
                //               statsData.cases.active,
                //               statsData.cases.total,
                //         ]
                //       }
                //     ]
                //   };
    
            })
        
        }

        // if (this.spieChart != null){
        //     this.spieChart.destroy();
        //     this.spieChart = undefined
        // }

    }
 

    return (
        <div>
            {
                getStats ? (<Pie
                    data={state}
                    options={{
                      title:{
                        display:true,
                        text:'Average Rainfall per month',
                        fontSize:20
                      },
                      legend:{
                        display:true,
                        position:'right'
                      }
                    }}
                  />) : 'Stats Data Not Available'
            }
        </div>
    )
}

export default ChartsComponent1
