import axios from 'axios';
import React, { useEffect, useState } from 'react';
import {Pie, Doughnut} from 'react-chartjs-2';

const ChartsComponent1 = ({country}) => {

    const [selectedcountry, setSelectedCountry] = useState(country);

    useEffect(() => {
        fetchChartData(selectedcountry)
    }, [selectedcountry])

    
    let statsData = {
};

    const duration = [
        'Last One Week',
        'Last Two Weeks',
        'Last Three Weeks',
        'Last One Month',
    ];

    const CHART_COLORS = {
        red: 'rgb(255, 99, 132)',
        orange: 'rgb(255, 159, 64)',
        yellow: 'rgb(255, 205, 86)',
        green: 'rgb(75, 192, 192)',
        blue: 'rgb(54, 162, 235)',
        purple: 'rgb(153, 102, 255)',
        grey: 'rgb(201, 203, 207)'
      };

    const state = {
      labels: ['Recovered', 'Critical', 'Active', 'New', 'Total'],
      datasets: [
        {
          label: 'Rainfall',
          backgroundColor: Object.values(CHART_COLORS),
          data: [
                statsData.cases.new,
                statsData.cases.critical,
                statsData.cases.recoverd,
                statsData.cases.active,
                statsData.cases.total,
          ]
        }
      ]
    };

    const fetchChartData = (country) => {
        if (country === null) {
            return
        }

        // if (this.spieChart != null){
        //     this.spieChart.destroy();
        //     this.spieChart = undefined
        // }

        axios.get(`/api/statistics/${country}`).then(res=>{
            if(res){

                statsData = {
                    country: res.data.response[0].country,
                    deaths: res.data.response[0].deaths,
                    tests: res.data.response[0].tests,
                    cases: res.data.response[0].cases,
                    lastUpdate: (new Date(res.data.response[0].time)).toUTCString()
                };

            }
        })
    }
 

    return (
        <div>
            <Pie
          data={state}
          options={{
            title:{
              display:true,
              text:'Average Rainfall per month',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />

        </div>
    )
}

export default ChartsComponent1
