import React, { Component } from 'react';
import {Chart} from 'chart.js';


class ChartsComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            country: props.country
        };

        this.statsData = {
            country: undefined,
            deaths: undefined,
            tests: undefined,
            cases: undefined,
            lastUpdate: undefined
        };

        this.duration = [
            'Last One Week',
            'Last Two Weeks',
            'Last Three Weeks',
            'Last One Month',
        ];

        this.selectedDuration = this.duration[0];
        this.historicData = [];
        this.sbarChart = undefined;
        this.spieChart = undefined;

        this.CHART_COLORS = {
            red: 'rgb(255, 99, 132)',
            orange: 'rgb(255, 159, 64)',
            yellow: 'rgb(255, 205, 86)',
            green: 'rgb(75, 192, 192)',
            blue: 'rgb(54, 162, 235)',
            purple: 'rgb(153, 102, 255)',
            grey: 'rgb(201, 203, 207)'
          };

        // this.createStatisticsPieChart = this.createStatisticsPieChart.bind(this)

    };

    createStatisticsPieChart = () => {
        let item = document.getElementById("spie");
        let colors = Chart.helpers.colors

        if(item != null){
            this.spieChart = new Chart(item.getContext('2d'), {
                type: 'pie',
                data: {
                    labels: ['Recovered', 'Critical', 'Active', 'New', 'Total'],
                    datasets: [{
                        backgroundColor: Object.values(colors(this.CHART_COLORS)),
                        data: [
                            this.s
                        ]
                    }]
                }

            })
        }
    }
    
    render() {
        return (
            <div className="container-fluid">
                <div className='row flex-justify-content charts-component'>
                    <div className='stats-bar cell-6 fg-grayBlue'>
                        <canvas id="sbar"></canvas>
                    </div>
                    <div className='stats-bar cell-6 fg-grayBlue'>
                        <canvas id="spie"></canvas>
                    </div>
                </div>
        
            </div>
        )
    }
};


export default ChartsComponent
