import React, { Component } from 'react';
import {Pie, Doughnut} from 'react-chartjs-2';
import axios from 'axios';


class ChartsComponent extends Component {
    constructor(props){
        super(props)
        // this.state = {
        //     country: props.country
        // };

        this.statsData = {
            country: undefined,
            deaths: undefined,
            tests: undefined,
            cases: undefined,
            lastUpdate: undefined
        };

        this.duration = [
            'Last One Week',
            'Last Two Weeks',
            'Last Three Weeks',
            'Last One Month',
        ];

        this.selectedDuration = this.duration[0];
        this.historicData = [];
        // this.sbarChart = undefined;
        this.spieChart = undefined;
        
        this.CHART_COLORS = {
            red: 'rgb(255, 99, 132)',
            orange: 'rgb(255, 159, 64)',
            yellow: 'rgb(255, 205, 86)',
            green: 'rgb(75, 192, 192)',
            blue: 'rgb(54, 162, 235)',
            purple: 'rgb(153, 102, 255)',
            grey: 'rgb(201, 203, 207)'
          };

          this.createStatisticsPieChart()

    };

    componentDidUpdate(){
        let country = this.props.country

        console.log('coun2' + country)
        this.fetchChartData(country)
        console.log('stat' + this.statsData.cases)
    };

    createStatisticsPieChart = () => {
       return this.spieChart = {
         labels: ['Recovered', 'Critical', 'Active', 'New', 'Total'],
         datasets: [
           {
             backgroundColor: Object.values(this.CHART_COLORS),
            //  data: [
            //        this.statsData.cases.new,
            //        this.statsData.cases.critical,
            //        this.statsData.cases.recoverd,
            //        this.statsData.cases.active,
            //        this.statsData.cases.total,
            //  ]
           }
         ]
       };
        
    }


    // createStatisticsPieChart(){
    //     let item = document.getElementById("spie").getContext('2d');
        
    //     if(item !== null){
    //         // let colors = Chart().helpers.colors
    //         this.spieChart = new Chart(item, {
    //             type: 'pie',
    //             data: {
    //                 labels: ['Recovered', 'Critical', 'Active', 'New', 'Total'],
    //                 datasets: [{
    //                     backgroundColor: Object.values(this.CHART_COLORS),
    //                     data: [
    //                         this.statsData.cases.new,
    //                         this.statsData.cases.critical,
    //                         this.statsData.cases.recoverd,
    //                         this.statsData.cases.active,
    //                         this.statsData.cases.total,
    //                     ]
    //                 }]
    //             },
    //             options: {
    //                 responsive: true,
    //                 plugins: {
    //                     legend: {
    //                         position: 'bottom',
    //                     },
    //                     title: {
    //                         display: true,
    //                         text: this.statsData.country + ' - Last Updated: ' + this.statsData.lastUpdate
    //                     }
    //                 }
    //             }

    //         })
    //     }
    // };

    
    
    
    fetchChartData = (country) => {
        if (country === null) {
            return
        }

        // if (this.spieChart != null){
        //     this.spieChart.destroy();
        //     this.spieChart = undefined
        // }

        axios.get(`/api/statistics/${country}`).then(res=>{
            if(res){

                console.log(res.data.response[0])

                this.statsData = {
                    country: res.data.response[0].country,
                    deaths: res.data.response[0].deaths,
                    tests: res.data.response[0].tests,
                    cases: res.data.response[0].cases,
                    lastUpdate: (new Date(res.data.response[0].time)).toUTCString()
                };
                

                this.createStatisticsPieChart()

            }
        })
    }
    
    render() {
        return (
            <div className="container-fluid">
                <div className='row flex-justify-content charts-component'>
                    <div className='stats-bar cell-6 fg-grayBlue'>
                        <canvas id="sbar"></canvas>
                    </div>
                    <div className='stats-bar cell-6 fg-grayBlue'>
                    <Pie
                    data={this.createStatisticsPieChart}
                    options={{
                      title:{
                        display:true,
                        text:'Average Rainfall per month',
                        fontSize:20
                      },
                      legend:{
                        display:true,
                        position:'right'
                      }
                    }}
                  />
                    </div>
                </div>
        
            </div>
        )
    }
};


export default ChartsComponent
