import React, { Component } from 'react';
import {Chart} from 'chart.js';
import axios from 'axios';


class ChartsComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            country: props.country
        };

        this.statsData = {
            country: undefined,
            deaths: undefined,
            tests: undefined,
            cases: undefined,
            lastUpdate: undefined
        };

        this.duration = [
            'Last One Week',
            'Last Two Weeks',
            'Last Three Weeks',
            'Last One Month',
        ];

        this.selectedDuration = this.duration[0];
        this.historicData = [];
        // this.sbarChart = undefined;
        this.spieChart = undefined;
        
        this.CHART_COLORS = {
            red: 'rgb(255, 99, 132)',
            orange: 'rgb(255, 159, 64)',
            yellow: 'rgb(255, 205, 86)',
            green: 'rgb(75, 192, 192)',
            blue: 'rgb(54, 162, 235)',
            purple: 'rgb(153, 102, 255)',
            grey: 'rgb(201, 203, 207)'
          };

        this.createStatisticsPieChart = this.createStatisticsPieChart.bind(this)

    };

    componentDidUpdate(){
        let country = this.props.getSelectedCountry()
        console.log('coun2' + country)
        this.fetchChartData(country)
    };

    createStatisticsPieChart = () => {
        this.state = {
         labels: ['Recovered', 'Critical', 'Active', 'New', 'Total'],
