                    //     tests: data.response[0].tests,
                    //     cases: data.response[0].cases,
                    //     lastUpdate: (new Date(data.response[0].time)).toUTCString()
                    // });
                    console.log('stats' + statsData)
    
                    setGetStats(true)
    
                }
    
                //     state = {
                //     labels: ['Recovered', 'Critical', 'Active', 'New', 'Total'],
                //     datasets: [
                //       {
                //         backgroundColor: Object.values(CHART_COLORS),
